import React, { Component } from 'react';
import {
  StyleSheet,
  View,
  Text,
  TouchableOpacity,
  Animated,
  ScrollView,
  Image,
  Dimensions,
  FlatList
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { connect } from 'react-redux';

import firebase from '../Firebase';

const { width } = Dimensions.get("window");

class Consultations extends Component {

  state = {
    users:[],
    active: 'Chats',
    xTabOne: 0,
    xTabTwo: 0,
    translateX: new Animated.Value(0),
    translateXTabOne: new Animated.Value(0),
    translateXTabTwo: new Animated.Value(width),
    translateY: -1000
  }


  componentDidMount() {
    const {profile} = this.props
    firebase.database().ref(`users/profiles/${profile.uid}/messages/`).child(profile.uid).on('child_added', (val) => {
      const users = val.val();
      users.uid = val.key;
      console.log(users)
      this.setState((prevState) => {
        return {
          users: [...prevState.users, users]
        }
      })
    })  
  }


  handleSlide = type => {
    let {
      active,
      xTabOne,
      xTabTwo,
      translateX,
      translateXTabOne,
      translateXTabTwo
    } = this.state;
    Animated.spring(translateX, {
      toValue: type,
      duration: 100
    }).start();
    if (active === 0) {
      Animated.parallel([
        Animated.spring(translateXTabOne, {
          toValue: 0,
          duration: 100
        }).start(),
        Animated.spring(translateXTabTwo, {
          toValue: width,
          duration: 100
        }).start()
      ]);
    } else {
      Animated.parallel([
        Animated.spring(translateXTabOne, {
          toValue: -width,
          duration: 100
        }).start(),
        Animated.spring(translateXTabTwo, {
          toValue: 0,
          duration: 100
        }).start()
      ]);
    }
  };


  renderRow = ({ item }) => {
    // console.log(item)
    const doctor = item;
    return (
      <TouchableOpacity
        onPress={() => this.props.navigation.navigate('ChatScreen', { doctor })}
        style={{ flexDirection: 'row', marginTop: 15 }}>
        <Image
          // source={{ uri: item.photo }}
          style={{ width: 80, height: 80, resizeMode: 'cover', borderRadius: 50, marginRight: 10 }}
        />
        <Text style={{ fontSize: 18, color: '#000' }}>{item.uid}</Text>
      </TouchableOpacity>
    )
  };

  render() {
    let {
      xTabOne,
      xTabTwo,
      translateX,
      active,
      translateXTabOne,
      translateXTabTwo,
      translateY,
      
    } = this.state;
    
    return (
      <View style={styles.contanier}>
        <View style={{ marginHorizontal: 13, top: 32 }}>
          <View style={{ flexDirection: 'row' }}>
            <Ionicons name='ios-arrow-back' size={30} />
            <View style={styles.iconSearch}>
              <Ionicons name='ios-search' size={30} style={styles.iconSearch} />
            </View>
          </View>
          <Text style={{ top: 23, fontSize: 32, fontWeight: 'bold' }}>Consultations</Text>

          <View style={styles.tabs}>
            <Animated.View
              style={{
                position: "absolute",
                width: "11%",
                height: "100%",
                top: 0,
                left: 0,
                borderBottomWidth: 3,
                borderBottomColor: '#1590f0',
                transform: [{ translateX }],

              }}
            />

            <TouchableOpacity style={styles.tab}
              onLayout={event => this.setState({ xTabOne: event.nativeEvent.layout.x })}
              onPress={() => this.setState({ active: 0 }, () => this.handleSlide(xTabOne))}
            >

              <Text style={{ color: '#000' }}>Chats</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.tab}
              onLayout={event => this.setState({ xTabTwo: event.nativeEvent.layout.x })}
              onPress={() => this.setState({ active: 1 }, () => this.handleSlide(xTabTwo))}
            >
              <Text style={{ color: '#000' }}>Calls</Text>
            </TouchableOpacity>
          </View>

          <ScrollView style={{ marginTop: 20 }}>
            <Animated.View
              style={{ transform: [{ translateX: translateXTabOne }] }}
              onLayout={event => this.setState({ translateY: event.nativeEvent.layout.height })}
            >
              <FlatList
                data={this.state.users}
                renderItem={this.renderRow}
                keyExtractor={(item) => item.uid}
              />
            </Animated.View>

            <Animated.View style={{ transform: [{ translateX: translateXTabTwo }, { translateY: -translateY }] }}>

            </Animated.View>
          </ScrollView>
        </View>

      </View>
    )
  }
}




const styles = StyleSheet.create({
  contanier: {
    flex: 1,
    backgroundColor: '#FFF'
  },
  iconSearch: {
    flex: 1,
    textAlign: 'right'
  },
  tabs: {
    borderBottomColor: 'gray',
    borderBottomWidth: StyleSheet.hairlineWidth,
    marginTop: 38,
    flexDirection: 'row',

  },
  tab: {
    marginRight: 25,
    paddingBottom: 18
  },

})

const mapStateToProps = ({authProfile}) => {
  return {
    profile: authProfile.profile,

  }
};

export default connect(mapStateToProps)(Consultations);
