        const uid = firebase.auth().currentUser.uid

        this.state.dbRef.child(uid).on('child_added', (val) => {
            let person = val.val();
            person.uid = val.key;

            this.setState((prevState) => {
                return {
                    users: [...prevState.users, person]
                }
            })
        })



    }


